xpath.script:

$(document).on('ajax:success', '.oe_product_cart .a-submit, .oe_product_cart .btn-primary', function() {
    var notification = document.getElementById('cartNotification');
    if (notification) {
        notification.style.display = 'block';
        setTimeout(function() {
            notification.style.display = 'none';
        }, 2500);
    }
});
