div.script:
document.addEventListener('DOMContentLoaded', function() {
    const tagItems = document.querySelectorAll('.tag-item');

    tagItems.forEach(function(item) {
        // Manejo de clicks en tags
        item.addEventListener('click', function() {
            const tagId = this.getAttribute('data-tag-id');

            // Construir la URL con el filtro de tag
            let params = new URLSearchParams();
            params.append('tag_id', tagId);

            // Mantener el filtro de envío gratis si está activo
            const currentUrl = new URL(window.location);
            if (currentUrl.searchParams.get('free_shipping') === 'true') {
                params.append('free_shipping', 'true');
            }

            // Redirigir a la nueva URL
            window.location.href = '/offers?' + params.toString();
        });

        // Manejo de eventos touch para móvil
        item.addEventListener('touchstart', function() {
            this.querySelector('.tag-underline').style.width = '100%';
            this.querySelector('.tag-underline').style.opacity = '1';
        });

        item.addEventListener('touchend', function() {
            setTimeout(() => {
                this.querySelector('.tag-underline').style.width = '0';
                this.querySelector('.tag-underline').style.opacity = '0';
            }, 300);
        });
    });
});

document.addEventListener('DOMContentLoaded', function() {
    // Toggle de envío gratis desktop
    var toggle = document.getElementById('freeShippingToggle');
    if (toggle) {
        toggle.checked = ;
        toggle.addEventListener('change', function() {
            var newUrl = '/offers?free_shipping=' + (this.checked ? 'true' : 'false');
            window.location.href = newUrl;
        });
    }

    // Toggle de envío gratis móvil
    var mobileToggle = document.getElementById('mobileFreeShippingToggle');
    if (mobileToggle) {
        mobileToggle.checked = ;
        mobileToggle.addEventListener('change', function() {
            var newUrl = '/offers?free_shipping=' + (this.checked ? 'true' : 'false');
            window.location.href = newUrl;
        });
    }
});

document.addEventListener('DOMContentLoaded', function() {
    var toggle = document.getElementById('freeShippingToggle');
    if (toggle) {
        // Establecer el estado inicial del checkbox
        toggle.checked = ;

        toggle.addEventListener('change', function() {
            var newUrl = '/offers?free_shipping=' + (this.checked ? 'true' : 'false');
            window.location.href = newUrl;
        });
    }
});

                        document.addEventListener('DOMContentLoaded', function () {
                            let offset = 9; // Número inicial de productos cargados
                            const limit = 9; // Número de productos a cargar por solicitud
                            const productsContainer = document.getElementById('products-container');
                            const loadingIndicator = document.getElementById('loading-indicator');

                            const observer = new IntersectionObserver((entries) => {
                                if (entries[0].isIntersecting) {
                                    loadMoreProducts();
                                }
                            }, {
                                root: null,
                                rootMargin: '0px',
                                threshold: 1.0
                            });

                            observer.observe(loadingIndicator);

                            function loadMoreProducts() {
                                loadingIndicator.style.display = 'block';

                                fetch(`/offers/load_more?offset=${offset}&limit=${limit}`)
                                    .then(response => response.json())
                                    .then(data => {
                                        data.products.forEach(product => {
                                            const productHTML = `









${product.name}


                                                                    $${product.discounted_price}
                                                                    $${product.list_price}





                                            `;
                                            productsContainer.insertAdjacentHTML('beforeend', productHTML);
                                        });

                                        offset += limit;
                                        loadingIndicator.style.display = 'none';

                                        if (!data.has_more) {
                                            observer.unobserve(loadingIndicator);
                                            loadingIndicator.style.display = 'none';
                                        }
                                    })
                                    .catch(error => {
                                        console.error('Error loading more products:', error);
                                        loadingIndicator.style.display = 'none';
                                    });
                            }
                        });
