t.script:
document.addEventListener('DOMContentLoaded', function() {
    function getPriceFromText(text) {
        return parseFloat((text || '').replace(/[^0-9.]/g, '')) || 0;
    }
    function updateTotal() {
        var total = 0;
        // Suma todos los productos seleccionados (incluyendo el principal y accesorios)
        document.querySelectorAll('input[name="bundle_product_ids[]"]:checked').forEach(function(cb) {
            // Busca el precio en la card correspondiente
            var priceDiv = null;
            if (cb.closest('.product-card')) {
                priceDiv = cb.closest('.product-card').querySelector('.mobile-main-price, .bundle-price');
            } else {
                // Es el producto principal (hidden)
                priceDiv = document.getElementById('main-bundle-price');
            }
            if (priceDiv) {
                total += getPriceFromText(priceDiv.textContent);
            }
        });
        document.getElementById('bundle-total').textContent = total.toLocaleString();
    }
    document.querySelectorAll('.bundle-checkbox').forEach(function(cb) {
        cb.addEventListener('change', updateTotal);
    });
    updateTotal();

    // Al enviar el formulario, solo se envían los productos seleccionados (checks marcados + el principal)
    document.getElementById('bundle-form').addEventListener('submit', function(e) {
        // No se necesita JS extra aquí, el backend debe manejar bundle_product_ids[]
    });

    // Comprar directo: envía los seleccionados y redirige a checkout
    document.getElementById('bundle-buy-now').addEventListener('click', function() {
        var form = document.getElementById('bundle-form');
        var data = new FormData();
        // Agrega todos los checkboxes marcados (incluyendo el hidden principal)
        form.querySelectorAll('input[name="bundle_product_ids[]"]:checked').forEach(function(cb) {
            data.append('bundle_product_ids[]', cb.value);
        });
        // Agrega los otros campos ocultos (csrf_token, product_id)
        form.querySelectorAll('input[type="hidden"]').forEach(function(input) {
            data.append(input.name, input.value);
        });
        fetch(form.action, {
            method: 'POST',
            body: data,
            credentials: 'same-origin'
        }).then(function(response) {
            if (response.ok) {
                window.location.href = '/shop/checkout?try_skip_step=true';
            }
        });
    });
});

document.addEventListener('DOMContentLoaded', function() {
    if (window.bootstrap && window.bootstrap.Carousel) {
        var el = document.getElementById('same-brand-carousel');
        if (el) {
            new window.bootstrap.Carousel(el, {interval: 0, wrap: true});
        }
    }
});

div.script:
document.addEventListener('DOMContentLoaded', function() {
    var mainImg = document.getElementById('main-product-img');
    var thumbs = document.querySelectorAll('#product-thumbs .product-thumb');
    // Cambiar imagen principal al hacer click en miniatura
    thumbs.forEach(function(thumb) {
        thumb.addEventListener('click', function() {
            mainImg.src = thumb.getAttribute('data-img');
            thumbs.forEach(function(t) { t.classList.remove('active'); });
            thumb.classList.add('active');
        });
    });
    // Al hacer click en la imagen principal, vuelve a la primera miniatura
    mainImg.addEventListener('click', function() {
        var firstThumb = document.querySelector('#product-thumbs .product-thumb');
        if (firstThumb) {
            mainImg.src = firstThumb.getAttribute('data-img');
            thumbs.forEach(function(t) { t.classList.remove('active'); });
            firstThumb.classList.add('active');
        }
    });
});

t.script:
document.addEventListener('DOMContentLoaded', function() {
    var qtyInput = document.getElementById('qty-input');
    var minusBtn = document.getElementById('qty-minus');
    var plusBtn = document.getElementById('qty-plus');
    if (qtyInput && minusBtn && plusBtn) {
        var max = parseInt(qtyInput.getAttribute('max')) || 9999;
        minusBtn.addEventListener('click', function() {
            var val = parseInt(qtyInput.value) || 1;
            if (val > 1) qtyInput.value = val - 1;
        });
        plusBtn.addEventListener('click', function() {
            var val = parseInt(qtyInput.value) || 1;
            if (val < max) qtyInput.value = val + 1;
        });
        qtyInput.addEventListener('input', function() {
            var val = parseInt(qtyInput.value) || 1;
            if (val < 1) qtyInput.value = 1;
            if (val > max) qtyInput.value = max;
        });
    }
});

div.script:
document.addEventListener('DOMContentLoaded', function() {
    var qtyInput = document.getElementById('qty-input');
    var cartQty = document.getElementById('cart-qty');
    var buyQty = document.getElementById('buy-qty');
    var cartForm = document.getElementById('add-to-cart-form');
    var buyForm = document.getElementById('buy-now-form');

    cartForm.addEventListener('submit', function(e) {
        cartQty.value = qtyInput.value;
    });

    buyForm.addEventListener('submit', function(e) {
        e.preventDefault();
        buyQty.value = qtyInput.value;
        var data = new FormData(buyForm);
        fetch(buyForm.action, {
            method: 'POST',
            body: data,
            credentials: 'same-origin'
        })
        .then(response => {
            if (response.ok) {
                window.location.href = '/shop/checkout?try_skip_step=true';
            }
        });
    });
});
